import React from 'react'
import { useState } from 'react';
import Swal from 'sweetalert2';
function Edit({selectedemp,Employees,setEmployees,Setisedit,setselectedemp}) {

  const [Name, setName] = useState(selectedemp.Name);
  const [Email, setEmail] = useState(selectedemp.Email);
  const [Pnumber, setPnumber] = useState(selectedemp.Pnumber);
  const [Date, setDate] = useState(selectedemp.Date);




//   const handleEdit = (id) =>{
//     const [elements] = Employees.filter((employees)=>{
//         return employees.id ===id;
//     })
//     setselectedemp(elements);
// }


  const handleUpdate = (e) => {
    e.preventDefault();

    

    const id = selectedemp.length + 1;
    const newEmployee = {id, Name, Email, Pnumber, Date };
    if(!Name || !Email || !Pnumber || !Date){
      return Swal.fire({
        icon:'error',
        title:'error!',
        text: "All fields must be Filled !",
        timer:2000,
        showConfirmButton:true
      })
    }

    const updatedEmp = Employees.map((employee)=>{
      if(employee.id === selectedemp.id){
        return newEmployee;
      }
     return  employee;
    })
    setEmployees(updatedEmp);
    Setisedit(false);
  console.log(updatedEmp)
  Swal.fire({
    icon:"success",
    title:'success',
    showConfirmButton:false,
    text: ` ${Name} has been Updated !`,
    timer:2000
  })
  }

  return (
    <section>
    <div className='container'>
      
        <div className='row'>
          <div className='col-lg-6'>
          <div className='form-group'>
            <label>Name</label>
            <input type='name' className='form-control' value={Name} onChange={(e) => setName(e.target.value)} ></input>

            <label>Email</label>
            <input type='Email' className='form-control' value={Email} onChange={(e) => setEmail(e.target.value)} ></input>

            <label>Phone Number</label>
            <input type='number' className='form-control' value={Pnumber} onChange={(e) => setPnumber(e.target.value)} ></input>

            <label>Date</label>
            <input type='Date' className='form-control' value={Date} onChange={(e) => setDate(e.target.value)} ></input>
            <button onClick={handleUpdate}  className='btn btn-primary mt-3'>update</button>
            <button onClick={()=>Setisedit(false)}  className='btn btn-primary mt-3'>Cancel</button>

          </div>
          


          </div>
      </div>


    </div>


  </section>
  )
}

export default Edit
