import React, { useEffect } from 'react'
import { useState } from 'react'
function List({ Employees, setEmployees }) {
  const [Name, setName] = useState('');
  const [Email, setEmail] = useState('');
  const [Pnumber, setPnumber] = useState(null);
  const [Date, setDate] = useState(0);
  
  
  
  
  const handleadd = (e) => {
    e.preventDefault();

    const id = Employees.length + 1;
    const newEmployee = {id, Name, Email, Pnumber, Date };

    Employees.push(newEmployee);
    setEmployees(Employees);

    console.log(newEmployee);
  console.log(Employees);
  }
  
  return (

    <section>
      <div className='container'>
        
          <div className='row'>
            <div className='col-lg-6'>
            <div className='form-group' onSubmit={handleadd}>
              <label>Name</label>
              <input type='name' className='form-control' value={Name} onChange={(e) => setName(e.target.value)} ></input>

              <label>Email</label>
              <input type='Email' className='form-control' value={Email} onChange={(e) => setEmail(e.target.value)} ></input>

              <label>Phone Number</label>
              <input type='number' className='form-control' value={Pnumber} onChange={(e) => setPnumber(e.target.value)} ></input>

              <label>Date</label>
              <input type='Date' className='form-control' value={Date} onChange={(e) => setDate(e.target.value)} ></input>
              <button className='btn btn-primary mt-3'>Submit</button>

            </div>
            


            </div>
        </div>


      </div>


    </section>


  )
}

export default List
