import React, { useEffect } from 'react'


function List({ Employees, setisadding, handleDelete,Setisedit,handleEdit,Isedit,setislist }) {
  useEffect(()=>{
    if(Isedit){
        setislist(false);
    }
    else{setislist(true)}
},[Isedit])

  return (
    <div>

      <button onClick={() => setisadding(true)} className='btn btn-primary mt-3 mx-3'>Add Employee</button> <br></br>

      {Employees.length > 0 ? Employees.map((employee) => {

        return (
          <div key={employee.id}>
            <section className='container'>
              <div className='row'>
                <div  className='col-lg-6'>
                  {employee.id}  {employee.Name} {employee.Email} {employee.Pnumber} {employee.Date} 
                  {<button className='btn btn-danger' onClick={() => handleDelete(employee.id)}>Delete</button>} 
                  {<button className='btn btn-secondary mx-2' onClick={() =>handleEdit(employee.id)}>Edit</button>}

                </div>
              </div>


            </section>

          </div>
        )
      }) : "no Employees"}


    </div>
  )

}

export default List
